import React from "react";
import './style.scss';
import styled from 'styled-components'
import Morning from '../../images/sunrise.png'
import Evening from '../../images/sunset.png'
import Night from '../../images/night.png'

const CardContainer = styled.div`
    margin: 20px;
    width: 100%;
    max-width: 400px;
    height: 550px;
    position: relative;
    z-index: 5;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`

const CardBackground = styled.div`
    position: absolute;
    z-index: -1;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background: url(${clima}) center center;
    opacity: .8;
    width: 100%;
    height: 100%;
    border-radius: 20px;
    box-shadow: 2px 3px 12px 3px rgba(0,0,0,0.86);
`
const Degree = styled.h1`
    color: white;
    font-size: 5rem;
`

export default function WeitherCard(props){
    const [weither, setWeither] = React.useState('')
    let date = new Date()
    let hour = date.getHours()
    console.log('Hora: ', hour)
    console.log('Props: ', props)
    let clima = Morning;


    if(hour > 0 && hour < 12){
        console.log('Manh達')
    }if(hour > 12 && hour < 18){
        console.log('Tarde')
    }if(hour > 18 && hour < 24){
        console.log('Manh達')
    }

    console.log('Estado: ', weither)

    console.log(weither)

    return(
        <CardContainer>
            <CardBackground/>
            <Degree>{props.temp}</Degree>
        </CardContainer>
    )
}
