import React from 'react'
import styled from 'styled-components'
import Logo from '../../images/logo.png'
import {BsSearch} from 'react-icons/bs';

const NavbarDiv = styled.div`
    position: absolute;
    top: 0;
    width: 100%;
    height: 100px;
    display: flex;

    img{
        height: 40px;
        margin-top: 20px;
        margin-right: auto;
        padding: 5px;
    }

    @media screen and (max-width: 768px){
        flex-direction: column;
        justify-content: center;
        align-items: center;

        img{
            margin-right: 0;
        }
    }
`
const SearchBar = styled.div`
    width: 350px;
    height: 50px;
    margin-top: 20px;
    display: flex;
    border-bottom-style: solid;
    border-width: 1px;
    border-color: white;
    margin-right: 20px;
    justify-content: center;
    align-items: center;

    input{
        border: none;
        background: none;
        font-size: 20px;
        padding: 5px;
        width: 100%;
        max-width: 320px;
    }

    input:focus{
        outline: none;
    }
`

export default function Navbar(){
    return(
        <NavbarDiv>
            <img src={Logo} />
            <SearchBar>
                <input type="text" placeholder="Digite o lugar" />
                <BsSearch style={{color : 'white'}}/>
            </SearchBar>
        </NavbarDiv>
    )
}
