import React, { useEffect } from "react";
import './style.scss';
import styled from 'styled-components'
import Morning from '../../images/sunrise.png'
import Evening from '../../images/sunset.png'
import Night from '../../images/night.png'

const CardContainer = styled.div`
    margin: 20px;
    width: 100%;
    max-width: 400px;
    height: 550px;
    position: relative;
    z-index: 5;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`

const CardBackground = styled.div`
    position: absolute;
    z-index: -1;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    opacity: .8;
    width: 100%;
    height: 100%;
    border-radius: 20px;
    box-shadow: 2px 3px 12px 3px rgba(0,0,0,0.86);
`
const Degree = styled.h1`
    color: white;
    font-size: 2rem;
    margin: 5px;
`

const Degrees = styled.div`
    display: flex;
`

export default function WeitherCard(props){
    const [image, setImage] = React.useState(Morning)
    let date = new Date()
    let hour = date.getHours()
    console.log('Hora: ', hour)
    console.log('Props: ', props)

    useEffect(() => {
        if(hour > 0 && hour < 12){
            setImage(Morning)
        }if(hour > 12 && hour < 18){
            setImage(Evening)
        }if(hour > 18 && hour < 24){
            setImage(Night)
        }
    }, [image])

    console.log('Estado Imagem: ', image)

    return(
        <CardContainer>
            <CardBackground  style={{background : `url(${image}) center center`}}/>
            <Degrees>
                <Degree>{props.temp}</Degree>
                <Degree>{props.temp}</Degree>
            </Degrees>

        </CardContainer>
    )
}
