import React from "react";
import './style.scss';
import styled from 'styled-components'
import morning from '../../images/sunrise.png'

const CardContainer = styled.div`
    margin: 20px;
    width: 400px;
    height: 550px;
    position: relative;
    z-index: 5;
`

const CardBackground = styled.div`
    position: absolute;
    z-index: -1;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background: url(${morning}) center center;
    opacity: .6;
    width: 100%;
    height: 100%;
`

export default function WeitherCard(){
    const [weither, setWeither] = React.useState(false)
    let clima = "Manhã"
    let img = 'Nada'

    return(
        <CardContainer>
            <CardBackground />
            {clima}
            {img}
        </CardContainer>
    )
}
