import React, { useEffect } from "react";
import './style.scss';
import styled from 'styled-components'
import Morning from '../../images/sunrise.png'
import Evening from '../../images/sunset.png'
import Night from '../../images/night.png'
import {RiUmbrellaFill} from 'react-icons/ri';
import {ImDroplet} from 'react-icons/im';
import {BsArrowUp} from 'react-icons/bs';
import {BsArrowDown} from 'react-icons/bs';
import $ from "jquery";


const CardContainer = styled.div`
    margin: 20px;
    width: 100%;
    max-width: 400px;
    height: 550px;
    position: relative;
    z-index: 5;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    .drop {
        background:-webkit-gradient(linear,0% 0%,0% 100%, from(rgba(13,52,58,1) ), to(rgba(255,255,255,0.6))  );
        background: -moz-linear-gradient(top, rgba(13,52,58,1) 0%, rgba(255,255,255,.6) 100%);
        width:1px;
        height:89px;
        position: absolute;
        bottom:200px;
        -webkit-animation: fall .63s linear infinite;
        -moz-animation: fall .63s linear infinite;
        index: 9000 !important;
    }

    /* animate the drops*/
    @-webkit-keyframes fall {
        to {margin-top:900px;}
    }
    @-moz-keyframes fall {
        to {margin-top:900px;}
    }
`

const CardBackground = styled.div`
    position: absolute;
    z-index: -1;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    opacity: .6;
    width: 100%;
    height: 100%;
    border-radius: 20px;
    box-shadow: 2px 3px 12px 3px rgba(0,0,0,0.86);
`

const Description = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: left;
    width: 100%;
    margin-top: 180px;
    font-family: Helvetica;
`

const DayLocale = styled.div`
    display: flex;
    justify-content: space-between;
    margin-bottom: 10px;
    border-bottom: 1px solid rgba(255,255,255,.25);
`

const Day = styled.p`
    color: white;
    font-size: 30px;
    padding: 8px;
    margin: 0;
`

const Locale = styled.p`
    color: white;
    font-size: 30px;
    padding: 8px;
    margin: 0;
`

const Situation = styled.p`
    color: white;
    font-size: 20px;
    padding: 8px;
    margin: 0;
`
const Info = styled.div`
    display: flex;
    flex-direction: column;
`

const Rain = styled.div`
    display: flex;
    flex-direction: row;
`

const Degrees = styled.div`
    display: flex;
    flex-direction: row;
`

const Degree = styled.p`
    color: white;
    font-size: 2.5rem;
    margin: 1rem;
    font-family: HelveticaBold;
`

const Rains = styled.p`
    color: white;
    font-size: 2.5rem;
    margin: 1rem;
    font-family: HelveticaBold;
`

export default function WeitherCard(props){
    const [image, setImage] = React.useState(Morning)
    let date = new Date()
    let hour = date.getHours()

    useEffect(() => {
        if(hour >= 0 && hour <= 12){
            setImage(Morning)
        }if(hour >= 12 && hour <= 18){
            setImage(Evening)
        }if(hour >= 18 && hour <= 24){
            setImage(Night)
        }
    }, [image])

    // number of drops created.
    var nbDrop = 20; 

    // function to generate a random number range.
    function randRange( minNum, maxNum) {
    return (Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum);
    }

    // function to generate drops
    function createRain() {

        for(let i=1;i<nbDrop;i++) {
        var dropLeft = randRange(0,1600);
        var dropTop = randRange(-1000,1400);

        $('.rain').append('<div class="drop" id="drop'+i+'"></div>');
        $('#drop'+i).css('left',dropLeft);
        $('#drop'+i).css('top',dropTop);
        }

    }
    // Make it rain
    createRain();

    return(
        <CardContainer className='rain'>
            <CardBackground  style={{background : `url(${image}) center center`}}/>
            <Info>
                <Degrees>
                    <Degree><BsArrowUp style={{color : '#0DDFFF'}} /> {props.tempMin} °C</Degree>
                    <Degree><BsArrowDown style={{color : '#C20000'}}/> {props.tempMax} °C</Degree>
                </Degrees>
                <Rain>
                    <Rains><ImDroplet/> {props.precipitation} mm</Rains>
                    <Rains><RiUmbrellaFill/> {props.probability} %</Rains>
                </Rain>
            </Info>
            <Description>
                <DayLocale>
                    <Day>{props.date}</Day>
                    <Locale>{props.locale}</Locale>
                </DayLocale>
                <Situation>{props.text}</Situation>
            </Description>
        </CardContainer>
    )
}
