import './style.scss';
import styled from 'styled-components'
import Morning from '../../images/sunrise.png'
import Evening from '../../images/sunset.jpg'
import Night from '../../images/night.jpg'

const CardContainer = styled.div`
    margin: 20px;
    width: 400px;
    height: 550px;
    position: relative;
    z-index: 5;
`

const CardBackground = styled.div`
    position: absolute;
    z-index: -1;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background: url(${props => props.clima ? Morning : Evening}) center center;
    opacity: .5;
    width: 100%;
    height: 100%;
`

export default function WeitherCard(){
    const [weither, setWeither] = React.useState(false)
    let clima = true
