import React from 'react'
import styled from 'styled-components'
import Logo from '../../images/logo.png'
import {BsSearch} from 'react-icons/bs';
import DayTime from '../dayTime';

const NavbarDiv = styled.div`
    position: sticky;
    z-index: 9999;
    top: 0;
    width: 100%;
    height: 100px;
    display: flex;
    justify-content: space-around;
    background: #153EFF;

    img{
        height: 40px;
        margin-top: 20px;
        padding: 5px;
    }

    @media screen and (max-width: 992px){
        flex-direction: column;
        justify-content: center;
        align-items: center;
        background: none;
        margin-top: 100px;
        position: relative;

        img{
            margin-right: 0;
        }
    }
`
const SearchBar = styled.div`
    width: 100%;
    height: 50px;
    margin-top: 20px;
    display: flex;
    border-bottom-style: solid;
    border-width: 1px;
    border-color: white;
    justify-content: center;
    align-items: center;
    max-width: 300px;

    input{
        border: none;
        background: none;
        font-size: 20px;
        padding: 5px;
        width: 100%;
    }

    input::placeholder{
        color: white;
    }

    input:focus{
        outline: none;
    }

    @media screen and (max-width: 768px){
        margin-right: 0;
    }
`

const Unity = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: 100px;
    cursor: pointer;

    img{
        width: 100px;
        height: 100px;
    }
`

export default function Navbar(){
    return(
        <NavbarDiv id="nav">
            <img src={Logo} />
            <Unity onClick={() => {setUnity(!unity)}}>
            <img src={unity ? C : F}/>
            </Unity>
            <SearchBar>
                <input type="text" placeholder="Digite o lugar" />
                <BsSearch style={{color : 'white'}}/>
            </SearchBar>
        </NavbarDiv>
    )
}
