import './App.scss';
import WeitherCard from './components/weitherCard'
import Navbar from './components/navBar'
import mock from '../src/services/mock'
import React from 'react'
import Cloudy from '../src/images/cloudy.jpg'
import Rainy from '../src/images/rainy.jpg'
import Sunny from '../src/images/sunny.jpg'
import F from '../src/images/f.png'
import C from '../src/images/c.png'
import styled from 'styled-components'
import {BsSearch} from 'react-icons/bs';

const Unity = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 100px;
  cursor: pointer;

  img{
    width: 100px;
    height: 100px;
  }
` 

const SearchBar = styled.div`
    width: 100%;
    height: 50px;
    margin-top: 20px;
    display: flex;
    border-bottom-style: solid;
    border-width: 1px;
    border-color: white;
    justify-content: center;
    align-items: center;
    max-width: 300px;

    input{
        border: none;
        background: none;
        font-size: 20px;
        padding: 5px;
        width: 100%;
    }

    input::placeholder{
        color: white;
    }

    input:focus{
        outline: none;
    }

    @media screen and (max-width: 768px){
        margin-right: 0;
    }
`

function App() {

  let weather = new Array();
  let weatherOsasco = new Array();
  let weatherSaoPaulo = new Array();
  weatherOsasco.push(...mock[0].weather)
  weatherSaoPaulo.push(...mock[1].weather)
  mock.map((data) => weather.push(...data.weather))

  const [time, setTime] = React.useState('')
  const [unity, setUnity] = React.useState(true)

  let date = new Date()
  let hour = date.getHours()

  React.useEffect(() => {
      if(hour >= 0 && hour <= 12){
          setTime('morning')
      }if(hour >= 12 && hour <= 18){
          setTime('evening')
      }if(hour >= 18 && hour <= 24){
          setTime('night')
      }
  }, [time])

  return (
    <div className={`App ${time}`}>
      <Navbar>
      </Navbar>
        <Unity onClick={() => {setUnity(!unity)}}>
          <img src={unity ? C : F}/>
        </Unity>
        <SearchBar>
            <input type="text" placeholder="Digite o lugar" id="myInput" onChange={(event) => handleSearch(event)}/>
            <BsSearch style={{color : 'white'}}/>
        </SearchBar>
      <div className="states">
        {weatherOsasco.map((data, key) => <WeitherCard 
          class={"card"}
          key={key} 
          date={data.date} 
          text={data.text} 
          tempMax={unity ? data.temperature.max + `${unity ? ' °C' : ' °F'}` : Math.round(((data.temperature.max * 1.8) + 32)) + `${unity ? ' °C' : ' °F'}`} 
          tempMin={unity ? data.temperature.min + `${unity ? ' °C' : ' °F'}` : Math.round(((data.temperature.min * 1.8) + 32)) + `${unity ? ' °C' : ' °F'}`}
          precipitation={data.rain.precipitation}
          probability={data.rain.probability}
          locale={'Osasco'}
          imageWeather = {(data.rain.probability >= 50) ? Rainy : (data.rain.probability < 50 && data.rain.probability >= 30) ? Cloudy : (data.rain.probability <= 30) ? Sunny : null}
        />)}
        {weatherSaoPaulo.map((data, key) => <WeitherCard 
          class={"card"}
          key={key} 
          date={data.date} 
          text={data.text} 
          tempMax={unity ? data.temperature.max + `${unity ? ' °C' : ' °F'}` : Math.round(((data.temperature.max * 1.8) + 32)) + `${unity ? ' °C' : ' °F'}`} 
          tempMin={unity ? data.temperature.min + `${unity ? ' °C' : ' °F'}` : Math.round(((data.temperature.min * 1.8) + 32)) + `${unity ? ' °C' : ' °F'}`}
          precipitation={data.rain.precipitation}
          probability={data.rain.probability}
          locale={'São Paulo'}
          imageWeather = {(data.rain.probability >= 50) ? Rainy : (data.rain.probability < 50 && data.rain.probability >= 30) ? Cloudy : (data.rain.probability <= 30) ? Sunny : null}
        />)}
      </div>
    </div>
  );
}

export default App;
