import React from 'react'
import styled from 'styled-components'
import Logo from '../../images/logo.png'
import {BsSearch} from 'react-icons/bs';
import DayTime from '../dayTime';

const NavbarDiv = styled.div`
    position: sticky;
    z-index: 9999;
    top: 0;
    width: 100%;
    height: 100px;
    display: flex;
    justify-content: space-around;
    background: #4E7AC7;

    img{
        height: 40px;
        margin-top: 20px;
        padding: 5px;
    }

    @media screen and (max-width: 992px){
        flex-direction: column;
        justify-content: center;
        align-items: center;
        background: none;
        margin-top: 100px;
        position: relative;

        img{
            margin-right: 0;
        }
    }

    .onscroll{
        background: rgb(142,92,124);
        background: linear-gradient(90deg, rgba(142,92,124,1) 0%, rgba(61,32,55,1) 100%);
    }
`
const SearchBar = styled.div`
    width: 100%;
    height: 50px;
    margin-top: 20px;
    display: flex;
    border-bottom-style: solid;
    border-width: 1px;
    border-color: white;
    justify-content: center;
    align-items: center;
    max-width: 300px;

    input{
        border: none;
        background: none;
        font-size: 20px;
        padding: 5px;
        width: 100%;
    }

    input::placeholder{
        color: white;
    }

    input:focus{
        outline: none;
    }

    @media screen and (max-width: 768px){
        margin-right: 0;
    }
`

export default function Navbar(){

    const nav = document.getElementById('nav')
    console.log(nav)
    console.log(nav.offsetTop)

    return(
        <NavbarDiv id="nav">
            <img src={Logo} />
            <DayTime />
            <SearchBar>
                <input type="text" placeholder="Digite o lugar" />
                <BsSearch style={{color : 'white'}}/>
            </SearchBar>
        </NavbarDiv>
    )
}
